import Modal from "react-modal";
import React, { useState } from "react";

Modal.setAppElement("#root");

function ModalNotes(props) {
  // console.log(props)
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const [expValue, setExpValue] = useState(props.modalNotes.note.title);
  console.log(expValue)

  const changeNote = () => {
    console.log("hello world");
  };
  return (
    <div>
      <button onClick={() => setModalIsOpen(true)}>open modal</button>
      <Modal isOpen={modalIsOpen}>
        {console.log(props.modalNotes.note)}
        <div>{props.modalNotes.note.time}</div>
        <div>{props.modalNotes.note.title}</div>
        <div>{props.modalNotes.note.text}</div>

        <div>
          <label htmlFor="noteChange">
            <textarea
              name="noteChange"
              id="noteChange"
              cols="30"
              rows="10"
              value={props.modalNotes.note.title}
              onChange={(event) => setExpValue(expValue)}
            ></textarea>
          </label>
        </div>

        {/* <div>
          <label htmlFor="noteChange">
            <textarea
              type="text"
              name="name"
              id="name"
              value={this.state.text}
              onChange={(event) => this.changeInput(event.target.value)}
            ></textarea>
          </label>
        </div> */}

        <div>
          <button onClick={() => setModalIsOpen(false)}>close</button>
        </div>
      </Modal>
    </div>
  );
}

export default ModalNotes;
