import { render } from "@testing-library/react";
import React, { Component } from "react";
import ListNote from "./ListNote";

class UserForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "",
      title: "",
      notes: [],
    };
  }
  formSubmit(event) {
    event.preventDefault();
    this.props.callback(this.state.text, this.state.title);
    this.state.text = ""
    this.state.title = "";
  }
  changeInput(value) {
    this.setState({ text: value, title: this.state.title });
  }
  changeInputTitle(value) {
    this.setState({ title: value,text:this.state.text });
  }
  render() {
    return (
      <div className="form">
        <form onSubmit={(event) => this.formSubmit(event)}>
          <div>
            <div>
              <label htmlFor="title">
                <input
                  type="text"
                  name="title"
                  id="title"
                  value={this.state.title}
                  onChange={(event) => this.changeInputTitle(event.target.value)}
                ></input>
              </label>
            </div>
          </div>

          <div>
            <label htmlFor="name">
              <textarea
                type="text"
                name="name"
                id="name"
                value={this.state.text}
                onChange={(event) => this.changeInput(event.target.value)}
              ></textarea>
            </label>
          </div>
          <div>
            <button type="submit">Add</button>
          </div>
        </form>
      </div>
    );
  }
}

export default UserForm;
